CREATE TABLE `category`(`category_id` TINYINT UNSIGNED NOT NULL, `name` VARCHAR(25) NOT NULL, `last_update` TIMESTAMP NOT NULL);CREATE TABLE `city`(`city_id` SMALLINT UNSIGNED NOT NULL, `city` VARCHAR(50) NOT NULL, `country_id` SMALLINT UNSIGNED NOT NULL, `last_update` TIMESTAMP NOT NULL);CREATE TABLE `actor`(`actor_id` SMALLINT UNSIGNED NOT NULL, `first_name` VARCHAR(45) NOT NULL, `last_name` VARCHAR(45) NOT NULL, `last_update` TIMESTAMP NOT NULL, `testero` CHAR NULL);CREATE TABLE `country`(`country_id` SMALLINT UNSIGNED NOT NULL, `country` VARCHAR(50) NOT NULL, `last_update` TIMESTAMP NOT NULL);CREATE TABLE `address`(`address_id` SMALLINT UNSIGNED NOT NULL, `address` VARCHAR(50) NOT NULL, `address2` VARCHAR(50) NULL, `district` VARCHAR(20) NOT NULL, `city_id` SMALLINT UNSIGNED NOT NULL, `postal_code` VARCHAR(10) NULL, `phone` VARCHAR(20) NOT NULL, `location` GEOMETRY NOT NULL, `last_update` TIMESTAMP NOT NULL);